import React, { useState, useEffect, useRef } from 'react';
import Papa from 'papaparse';
import Dashboard from './components/Dashboard';
import HashtagsTrending from './components/HashtagsTrending';
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [currentDataSource, setCurrentDataSource] = useState('');
  const [dataSourceIndex, setDataSourceIndex] = useState(0);
  const [switchCount, setSwitchCount] = useState(0);
  const [countdown, setCountdown] = useState(10);
  const intervalRef = useRef(null);
  const countdownRef = useRef(null);

  // Define the sequence of data sources
  const dataSources = [
    { 
      name: 'Network Analysis (Primary)', 
      url: '/nodes_export_for_frontend (1).csv', 
      type: 'network',
      description: 'Initial network topology from Gephi'
    },
    { 
      name: 'User Metrics (Secondary)', 
      url: '/fake_user_metrics.csv', 
      type: 'metrics',
      description: 'Updated user behavior metrics from Gephi'
    },
    { 
      name: 'User Network Metrics (Tertiary)', 
      url: '/user_network_metrics.csv', 
      type: 'network',
      description: 'Latest network analysis from Gephi'
    }
  ];

  // Function to load data from current source
  const loadDataFromSource = async (sourceIndex) => {
    const source = dataSources[sourceIndex];
    setLoading(true);
    setCurrentDataSource(source.name);
    
    try {
      const response = await fetch(source.url);
      const csvText = await response.text();
      
      Papa.parse(csvText, {
        header: true,
        complete: (results) => {
          let processedData = [];
          
          if (source.type === 'network') {
            // Process network data (similar to original logic)
            processedData = results.data.filter(row => 
              row.Label && row.Label !== 'Unnamed: 0' && 
              row['Closeness Centrality'] !== '0.0'
            ).map(row => ({
              ...row,
              'Closeness Centrality': parseFloat(row['Closeness Centrality']) || 0,
              'Betweenness Centrality': parseFloat(row['Betweenness Centrality']) || 0,
              'Eccentricity': parseInt(row['Eccentricity']) || 0,
              'Modularity Class': parseInt(row['Modularity Class']) || 0,
              dataSource: source.name,
              switchCount: switchCount
            }));
          } else if (source.type === 'metrics') {
            // Process metrics data
            processedData = results.data.filter(row => 
              row.username && row.metric_value
            ).map(row => ({
              ...row,
              dataSource: source.name,
              switchCount: switchCount
            }));
          }
          
          setData(processedData);
          setLoading(false);
          console.log(`Switched to data source: ${source.name} with ${processedData.length} records`);
        },
        error: (error) => {
          console.error(`Error parsing ${source.name}:`, error);
          setLoading(false);
        }
      });
    } catch (error) {
      console.error(`Error loading ${source.name}:`, error);
      setLoading(false);
    }
  };

  // Function to switch to next data source
  const switchToNextDataSource = () => {
    const nextIndex = (dataSourceIndex + 1) % dataSources.length;
    setDataSourceIndex(nextIndex);
    setSwitchCount(prev => prev + 1);
    setCountdown(10); // Reset countdown
    loadDataFromSource(nextIndex);
  };

  useEffect(() => {
    // Load initial data from first source
    loadDataFromSource(0);
    
    // Set up timer to switch data sources every 10 seconds
    intervalRef.current = setInterval(() => {
      switchToNextDataSource();
    }, 10000);

    // Cleanup interval on unmount
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      if (countdownRef.current) {
        clearInterval(countdownRef.current);
      }
    };
  }, []);

  // Countdown timer effect
  useEffect(() => {
    countdownRef.current = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          return 10; // Reset to 10 when switching
        }
        return prev - 1;
      });
    }, 1000);

    return () => {
      if (countdownRef.current) {
        clearInterval(countdownRef.current);
      }
    };
  }, [dataSourceIndex]);

  const renderTabContent = () => {
    switch (activeTab) {
      case 'hashtags':
        return <HashtagsTrending />;
      case 'dashboard':
      default:
        return <Dashboard data={data} />;
    }
  };

  return (
    <div className="App">
      {/* Data Source Status Header */}
      <div className="data-source-header">
        <div className="source-info">
          <h2>üîÑ Dynamic Data Source Switching</h2>
          <div className="current-source">
            <span className="source-label">üìä Current Source:</span>
            <span className="source-name">{currentDataSource}</span>
          </div>
          <div className="source-description">
            {dataSources[dataSourceIndex]?.description}
          </div>
        </div>
        
        <div className="switching-info">
          <div className="switch-counter">
            <span className="counter-label">Data Source Switches:</span>
            <span className="counter-value">{switchCount}</span>
          </div>
          <div className="next-switch">
            <span className="next-label">Next switch in:</span>
            <span className="timer">{countdown}s</span>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="tab-navigation">
        <button 
          className={`tab-button ${activeTab === 'dashboard' ? 'active' : ''}`}
          onClick={() => setActiveTab('dashboard')}
        >
          üõ°Ô∏è OSINT Dashboard
        </button>
        <button 
          className={`tab-button ${activeTab === 'hashtags' ? 'active' : ''}`}
          onClick={() => setActiveTab('hashtags')}
        >
          üî• Live Hashtags Trending
        </button>
      </div>
      
      {/* Loading indicator */}
      {loading && activeTab === 'dashboard' ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Switching to new data source: {currentDataSource}</p>
        </div>
      ) : (
        renderTabContent()
      )}
    </div>
  );
}

export default App;
