import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import './HashtagsTrending.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const HashtagsTrending = () => {
  const [hashtagsData, setHashtagsData] = useState([]);
  const [rawData, setRawData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const [activeTab, setActiveTab] = useState('hashtags');
  const [selectedPost, setSelectedPost] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
  };

  const switchTab = (tab) => {
    setActiveTab(tab);
  };

  const openPostDetails = (postIndex) => {
    setSelectedPost(rawData[postIndex]);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedPost(null);
  };

  useEffect(() => {
    const loadHashtagsData = async () => {
      try {
        const response = await fetch('/data_97cd739e_split_1.csv');
        const csvText = await response.text();
        
        Papa.parse(csvText, {
          header: true,
          complete: (results) => {
            // Store raw data for detailed view
            const filteredRawData = results.data.filter(row => row.hashtags && row.shares && row.likes);
            
            // Process hashtags data with original index mapping
            const processedData = filteredRawData
              .map((row, originalIndex) => {
                // Parse hashtags from the string format "['#hashtag1', '#hashtag2']"
                const hashtags = row.hashtags
                  .replace(/[\[\]']/g, '') // Remove brackets and quotes
                  .split(', ')
                  .filter(tag => tag.trim() !== '');
                
                return {
                  originalIndex: originalIndex,
                  hashtags: hashtags,
                  shares: parseFloat(row.shares) || 0,
                  likes: parseInt(row.likes) || 0,
                  comments: parseInt(row.comments_count) || 0,
                  platform: row.platform,
                  username: row.username,
                  content: row.content_text
                };
              })
              .sort((a, b) => b.shares - a.shares)
              .slice(0, 20);

            setRawData(filteredRawData);
            setHashtagsData(processedData);
            setLoading(false);
          },
          error: (error) => {
            console.error('Error parsing CSV:', error);
            setLoading(false);
          }
        });
      } catch (error) {
        console.error('Error loading CSV:', error);
        setLoading(false);
      }
    };

    loadHashtagsData();
  }, []);

  if (loading) {
    return (
      <div className={`hashtags-loading ${isDarkTheme ? 'dark-theme' : 'light-theme'}`}>
        <div className="loading-spinner"></div>
        <p>Loading trending hashtags...</p>
      </div>
    );
  }

  return (
    <div className={`hashtags-trending ${isDarkTheme ? 'dark-theme' : 'light-theme'}`}>
      <div className="hashtags-header">
        <div className="header-content">
          <div className="header-text">
            <h2>🔥 Live Hashtags Trending</h2>
            <p>Top 20 hashtags by social media engagement</p>
          </div>
          <button className="theme-toggle" onClick={toggleTheme}>
            {isDarkTheme ? '☀️ Light Mode' : '🌙 Dark Mode'}
          </button>
        </div>
      </div>

      <div className="tabs-container">
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 'hashtags' ? 'active' : ''}`}
            onClick={() => switchTab('hashtags')}
          >
            📊 Hashtags View
          </button>
          <button 
            className={`tab ${activeTab === 'graph' ? 'active' : ''}`}
            onClick={() => switchTab('graph')}
          >
            📈 Engagement Graph
          </button>
        </div>
      </div>

      {activeTab === 'hashtags' && (
        <div className="hashtags-grid">
          {hashtagsData.map((item, index) => (
            <div key={index} className="hashtag-card">
              <div className="hashtag-rank">#{index + 1}</div>
              <div className="hashtag-content">
                <div className="hashtags-list">
                  {item.hashtags.map((hashtag, tagIndex) => (
                    <span key={tagIndex} className="hashtag-tag">{hashtag}</span>
                  ))}
                </div>
                <div className="hashtag-content-text">
                  <p>{item.content}</p>
                </div>
                <div className="hashtag-stats">
                  <span className="shares-count">📤 {item.shares.toLocaleString()} shares</span>
                  <span className="likes-count">❤️ {item.likes.toLocaleString()} likes</span>
                </div>
                <div className="hashtag-meta">
                  <span className="platform">📱 {item.platform}</span>
                  <span className="username">👤 {item.username}</span>
                </div>
                <div className="hashtag-actions">
                  <button 
                    className="view-details-btn"
                    onClick={() => openPostDetails(item.originalIndex)}
                  >
                    🔍 View Details
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {activeTab === 'graph' && (
        <div className="graph-container">
          <div className="graph-card">
            <h3>📈 Top Posts Engagement Metrics</h3>
            <p>Likes, Shares, and Comments comparison for top 20 posts</p>
            <div className="chart-wrapper">
              <Line 
                data={{
                  labels: hashtagsData.map((_, index) => `#Post${index + 1}`),
                  datasets: [
                    {
                      label: 'Likes',
                      data: hashtagsData.map(item => item.likes),
                      borderColor: '#10b981',
                      backgroundColor: 'rgba(16, 185, 129, 0.1)',
                      borderWidth: 3,
                      tension: 0.4,
                      pointBackgroundColor: '#10b981',
                      pointBorderColor: '#ffffff',
                      pointBorderWidth: 2,
                      pointRadius: 6,
                      pointHoverRadius: 8,
                    },
                    {
                      label: 'Shares',
                      data: hashtagsData.map(item => item.shares),
                      borderColor: '#3b82f6',
                      backgroundColor: 'rgba(59, 130, 246, 0.1)',
                      borderWidth: 3,
                      tension: 0.4,
                      pointBackgroundColor: '#3b82f6',
                      pointBorderColor: '#ffffff',
                      pointBorderWidth: 2,
                      pointRadius: 6,
                      pointHoverRadius: 8,
                    },
                    {
                      label: 'Comments',
                      data: hashtagsData.map(item => item.comments),
                      borderColor: '#8b5cf6',
                      backgroundColor: 'rgba(139, 92, 246, 0.1)',
                      borderWidth: 3,
                      tension: 0.4,
                      pointBackgroundColor: '#8b5cf6',
                      pointBorderColor: '#ffffff',
                      pointBorderWidth: 2,
                      pointRadius: 6,
                      pointHoverRadius: 8,
                    }
                  ]
                }}
                options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  plugins: {
                    legend: {
                      position: 'top',
                      labels: {
                        color: isDarkTheme ? '#f8fafc' : '#1e293b',
                        font: {
                          size: 14,
                          weight: 'bold'
                        },
                        usePointStyle: true,
                        padding: 20
                      }
                    },
                    title: {
                      display: false
                    }
                  },
                  scales: {
                    x: {
                      grid: {
                        color: isDarkTheme ? 'rgba(248, 250, 252, 0.1)' : 'rgba(30, 41, 59, 0.1)',
                      },
                      ticks: {
                        color: isDarkTheme ? '#f8fafc' : '#1e293b',
                        font: {
                          size: 12
                        }
                      }
                    },
                    y: {
                      grid: {
                        color: isDarkTheme ? 'rgba(248, 250, 252, 0.1)' : 'rgba(30, 41, 59, 0.1)',
                      },
                      ticks: {
                        color: isDarkTheme ? '#f8fafc' : '#1e293b',
                        font: {
                          size: 12
                        },
                        callback: function(value) {
                          return value.toLocaleString();
                        }
                      }
                    }
                  },
                  interaction: {
                    intersect: false,
                    mode: 'index'
                  }
                }}
              />
            </div>
          </div>
        </div>
      )}
      
      {hashtagsData.length === 0 && activeTab === 'hashtags' && (
        <div className="no-data">
          <p>No trending hashtags found</p>
        </div>
      )}

      {/* Post Details Modal */}
      {showModal && selectedPost && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>📄 Post Details</h2>
              <button className="modal-close" onClick={closeModal}>✕</button>
            </div>
            <div className="modal-body">
              <div className="detail-grid">
                {Object.entries(selectedPost).map(([key, value]) => (
                  <div key={key} className="detail-item">
                    <div className="detail-label">{key.replace(/_/g, ' ').toUpperCase()}:</div>
                    <div className="detail-value">
                      {key === 'profile_link' || key === 'media_url' || key.includes('url') ? (
                        value ? <a href={value} target="_blank" rel="noopener noreferrer">{value}</a> : 'N/A'
                      ) : key === 'content_text' ? (
                        <div className="content-text">{value || 'N/A'}</div>
                      ) : key === 'hashtags' ? (
                        <div className="hashtags-display">
                          {value ? value.replace(/[\[\]']/g, '').split(', ').map((tag, i) => (
                            <span key={i} className="hashtag-chip">{tag}</span>
                          )) : 'N/A'}
                        </div>
                      ) : key === 'mentions' ? (
                        <div className="mentions-display">
                          {value ? value.replace(/[\[\]']/g, '').split(', ').map((mention, i) => (
                            <span key={i} className="mention-chip">{mention}</span>
                          )) : 'N/A'}
                        </div>
                      ) : (
                        <span>{value || 'N/A'}</span>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HashtagsTrending;
